package exercice.Travaux.SiP37;import java.util.*;public class Exercice {public static List<String> solution(String texte, List<Character> ordre) {List<String> mots = Arrays.asList(texte.split(" "));mots.sort(new ComparateurOrdreFixe(ordre));return mots;}private static class ComparateurOrdreFixe implements Comparator<String> {private Map<Character, Integer> indexMap = new HashMap<>();public ComparateurOrdreFixe(List<Character> ordreFixe) {for (int i = 0; i < ordreFixe.size(); i++) {indexMap.put(ordreFixe.get(i), i);}}@Override public int compare(String mot1, String mot2) {char premierLettre1 = mot1.charAt(0);char premierLettre2 = mot2.charAt(0);Integer index1 = indexMap.getOrDefault(premierLettre1, Integer.MAX_VALUE);Integer index2 = indexMap.getOrDefault(premierLettre2, Integer.MAX_VALUE);int result = index1.compareTo(index2);if (result == 0) {return mot1.compareTo(mot2);}return result;}}}
